function 加密函数(代码) {
    let 结果 = '';
    // 遍历代码的每一个字符
    for (let 我 = 0; 我 < 代码.length; 我++) {
        let 字符编码 = 代码.charCodeAt(我);
        // 如果字符是大写字母
        if (字符编码 >= 65 && 字符编码 <= 90) {
            // 将字符编码转换为ROT13
            结果 += String.fromCharCode(((字符编码 - 65 + 13) % 26) + 65);
        // 如果字符是小写字母
        } else if (字符编码 >= 97 && 字符编码 <= 122) {
            结果 += String.fromCharCode(((字符编码 - 97 + 13) % 26) + 97);
        } else {
            // 其他字符保持不变
            结果 += 代码.charAt(我);
        }
    }
    // 返回结果
    return 结果;
}

